import React, { useContext, useState } from 'react'
import { Helmet } from 'react-helmet-async';
import Form from 'react-bootstrap/Form'
import { Button } from '@mui/material';
import { Store } from '../Store';

export default function ProfileScreen() {

    const{state, dispatch: ctxDispatch } = useContext(Store);
    const {userInfo } = state;
    const [name, setName] = useState(userInfo.name);
    const [email, setEmail] = useState(userInfo.email);
    const [password, setPassword] = useState('');
    const [confirmpassword, setConfirmPassword] = useState('');
    const submitHandler = async () => {}



  return (
    <div className='container small-container'>
        <Helmet>User Profile</Helmet>

        <h1 className='my-3'>User Profile</h1>
        <form onSubmit={submitHandler}>
            <Form.Group className='m-3' control='name'>
                <Form.Label>Name</Form.Label>
                <Form.Control value={name} onChange={(e) => setName(e.target.value)} required/>
            </Form.Group>

            <Form.Group className='m-3' control='email'>
                <Form.Label>Email</Form.Label>
                <Form.Control value={email} onChange={(e) => setEmail(e.target.value)} required/>
            </Form.Group>

            <Form.Group className='m-3' control='password'>
                <Form.Label>Password</Form.Label>
                <Form.Control type='password' onChange={(e) => setPassword(e.target.value)} required/>
            </Form.Group>

            <Form.Group className='m-3' control='password'>
                <Form.Label>Confirm Password</Form.Label>
                <Form.Control type='password' onChange={(e) => setConfirmPassword(e.target.value)} required/>
            </Form.Group>

            <div className='mb-3'>
                <Button type='submit' variant='outlined' colo>Update</Button>

            </div>

        </form>

    </div>
  )
}
