import * as React from 'react';
import Box from '@mui/material/Box';
import Tabs, { tabsClasses } from '@mui/material/Tabs';
import Tab from '@mui/material/Tab';



export default function ScrollBar() {

  const [value, setValue] = React.useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <Box
      sx={{
        flexGrow: 1,
        maxWidth: { xs: 320, sm: 480},
        bgcolor: 'white',
        color: 'gold',
      }}
    >
      <Tabs
        value={value}
        onChange={handleChange}
        centered
        variant="scrollable"
        scrollButtons
        aria-label="visible arrows tabs example"
        sx={{
          [`& .${tabsClasses.scrollButtons}`]: {
            '&.Mui-disabled': { opacity: 0.8 },
          },
        }}
      >
        <Tab className = 'scroll_tabs' label="Item One" />
        <Tab className = 'scroll_tabs' label="Item Two" />
        <Tab className = 'scroll_tabs' label="Item Three" />
        <Tab className = 'scroll_tabs' label="Item Four" />
        <Tab className = 'scroll_tabs' label="Item Five" />
        <Tab className = 'scroll_tabs' label="Item Six" />
        <Tab className = 'scroll_tabs' label="Item Seven" />
      </Tabs>
    </Box>
  );
}
